'use client'

import type { Form as FormType } from '@payloadcms/plugin-form-builder/types'

import { useRouter, useSearchParams } from 'next/navigation'
import React, { useCallback, useState, useMemo } from 'react'
import { useForm, FormProvider } from 'react-hook-form'
import RichText from '@/components/RichText'
import { Button } from '@/components/ui/button'
import { Media } from '@/components/Media'

import { fields } from './fields'
import { getClientSideURL } from '@/utilities/getURL'
import { DefaultTypedEditorState } from '@payloadcms/richtext-lexical'
import { Media as MediaType } from '@/payload-types'

export type FormBlockType = {
  blockName?: string
  blockType?: 'formBlock'
  enableIntro: boolean
  form: FormType
  introContent?: DefaultTypedEditorState
  formImage?: MediaType | number
  alignment?: 'left' | 'right'
}

export const FormBlock: React.FC<
  {
    id?: string
  } & FormBlockType
> = (props) => {
  const {
    enableIntro,
    form: formFromProps,
    form: { id: formID, confirmationMessage, confirmationType, redirect, submitButtonLabel } = {},
    introContent,
    formImage,
    alignment,
  } = props

  const searchParams = useSearchParams()

  const defaultValues = useMemo(() => {
    const values: Record<string, string | undefined> = {}

    if (!formFromProps.fields) return values

    const destination = searchParams.get('destination')
    const dateFrom = searchParams.get('dateFrom')
    const dateTo = searchParams.get('dateTo')
    const tourType = searchParams.get('tourType')

    // Map query params to form fields
    formFromProps.fields.forEach((field) => {
      if (!('name' in field) || !field.name) return

      const fieldName = field.name.toLowerCase()
      const blockType = 'blockType' in field ? field.blockType : undefined

      // Set default value from field if it exists
      if (
        'defaultValue' in field &&
        field.defaultValue !== undefined &&
        field.defaultValue !== null
      ) {
        values[field.name] = String(field.defaultValue)
      }

      if (
        destination &&
        (fieldName.includes('destination') || (blockType as string) === 'destinationSelect')
      ) {
        values[field.name] = destination
      }

      if (tourType && (fieldName.includes('tour') || fieldName.includes('category'))) {
        values[field.name] = tourType
      }

      if (dateFrom && (blockType as string) === 'dateRange') {
        values[field.name] = dateTo ? `${dateFrom},${dateTo}` : dateFrom
      } else if (
        dateFrom &&
        (blockType === 'date' || fieldName.includes('date') || fieldName.includes('when'))
      ) {
        values[field.name] = dateFrom
      }
    })

    return values
  }, [formFromProps.fields, searchParams])

  const formMethods = useForm({
    defaultValues,
  })

  const {
    control,
    formState: { errors },
    handleSubmit,
    register,
    reset,
  } = formMethods

  // Reset form when query params change
  React.useEffect(() => {
    reset(defaultValues)
  }, [defaultValues, reset])

  const [isLoading, setIsLoading] = useState(false)
  const [hasSubmitted, setHasSubmitted] = useState<boolean>()
  const [error, setError] = useState<{ message: string; status?: string } | undefined>()
  const router = useRouter()

  const onSubmit = useCallback(
    (data: Record<string, string | undefined>) => {
      let loadingTimerID: ReturnType<typeof setTimeout>
      const submitForm = async () => {
        setError(undefined)

        const dataToSend = Object.entries(data).map(([name, value]) => ({
          field: name,
          value,
        }))

        // delay loading indicator by 1s
        loadingTimerID = setTimeout(() => {
          setIsLoading(true)
        }, 1000)

        try {
          const req = await fetch(`${getClientSideURL()}/api/form-submissions`, {
            body: JSON.stringify({
              form: formID,
              submissionData: dataToSend,
            }),
            headers: {
              'Content-Type': 'application/json',
            },
            method: 'POST',
          })

          const res = await req.json()

          clearTimeout(loadingTimerID)

          if (req.status >= 400) {
            setIsLoading(false)

            setError({
              message: res.errors?.[0]?.message || 'Internal Server Error',
              status: res.status,
            })

            return
          }

          setIsLoading(false)
          setHasSubmitted(true)

          if (confirmationType === 'redirect' && redirect) {
            const { url } = redirect

            const redirectUrl = url

            if (redirectUrl) router.push(redirectUrl)
          }
        } catch (err) {
          console.warn(err)
          setIsLoading(false)
          setError({
            message: 'Something went wrong.',
          })
        }
      }

      void submitForm()
    },
    [router, formID, redirect, confirmationType],
  )

  const imageContent = formImage && typeof formImage !== 'number' && (
    <div className="rounded-xl w-full h-full bg-muted order-1 md:order-none">
      <div className="relative max-h-[500px] overflow-hidden w-full">
        <Media resource={formImage} className="w-full h-full object-cover" />
        <div className="absolute left-0 -bottom-1 right-0 w-full">
          <svg
            className="w-full h-auto"
            viewBox="0 0 677 34"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            xmlnsXlink="http://www.w3.org/1999/xlink"
          >
            <rect
              y="33.2163"
              width="33.2163"
              height="677"
              transform="rotate(-90 0 33.2163)"
              fill="url(#pattern0_248_389)"
            />
            <defs>
              <pattern
                id="pattern0_248_389"
                patternContentUnits="objectBoundingBox"
                width="1"
                height="1"
              >
                <use
                  xlinkHref="#image0_248_389"
                  transform="matrix(0.0339693 0 0 0.00166667 -0.179386 0)"
                />
              </pattern>
              <image
                id="image0_248_389"
                width="40"
                height="600"
                preserveAspectRatio="none"
                xlinkHref="data:image/png;base64,"
              />
            </defs>
          </svg>
        </div>
      </div>
      <div className="p-[51px]">
        {formImage.caption && (
          <RichText
            className="!p-0 text-black prose-h2:text-2xl prose-h2:mb-3"
            style={{ padding: 0 }}
            data={formImage.caption}
          />
        )}
      </div>
    </div>
  )

  const formContent = (
    <div className="p-4 lg:p-6">
      {enableIntro && introContent && !hasSubmitted && (
        <RichText className="mb-8 lg:mb-12" data={introContent} enableGutter={false} />
      )}

      <FormProvider {...formMethods}>
        {!isLoading && hasSubmitted && confirmationType === 'message' && (
          <RichText data={confirmationMessage} />
        )}
        {isLoading && !hasSubmitted && <p>Loading, please wait...</p>}
        {error && <div>{`${error.status || '500'}: ${error.message || ''}`}</div>}
        {!hasSubmitted && (
          <form id={formID} onSubmit={handleSubmit(onSubmit)}>
            <div className="flex flex-wrap gap-5">
              {formFromProps &&
                formFromProps.fields &&
                formFromProps.fields?.map((field, index) => {
                  // eslint-disable-next-line @typescript-eslint/no-explicit-any
                  const Field: React.FC<any> = fields?.[field.blockType as keyof typeof fields]
                  if (Field) {
                    return (
                      <Field
                        key={index}
                        form={formFromProps}
                        {...field}
                        {...formMethods}
                        control={control}
                        errors={errors}
                        register={register}
                      />
                    )
                  }
                  return null
                })}
            </div>

            <Button form={formID} type="submit" variant="default" className="mt-6 w-full">
              {submitButtonLabel}
            </Button>
          </form>
        )}
      </FormProvider>
    </div>
  )

  return (
    <div className="container">
      {formImage && typeof formImage !== 'number' ? (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-8 items-center">
          {alignment === 'right' ? (
            <>
              {formContent}
              {imageContent}
            </>
          ) : (
            <>
              {imageContent}
              {formContent}
            </>
          )}
        </div>
      ) : (
        <div className="lg:max-w-[48rem] mx-auto">{formContent}</div>
      )}
    </div>
  )
}
